// Base64 encoded short phone ring sound
export const phoneRingBase64 = "data:audio/mp3;base64,";

// Define a type for the return value of playRingSound
export const playRingSound = () => {
  try {
    const audio = new Audio(phoneRingBase64);
    audio.volume = 0.5;
    
    // Try to play, but handle errors silently
    audio.play().catch(err => {
      console.error("Error playing audio:", err);
    });
    
    return audio;
  } catch (error) {
    console.error("Error creating audio:", error);
    // Return a mock audio object that does nothing
    return {
      pause: () => {},
      volume: 0.5
    };
  }
}; 